
class RubtidCalc
{constructor(id){this.calc=document.querySelector(id);this.input=this.calc.querySelector("input");this.calc.querySelectorAll(`.${this.calc.id}__number`).forEach(item=>{item.addEventListener("click",()=>{this.input.value+=item.innerText;})});this.calc.querySelector(`.${this.calc.id}__clear`).addEventListener("click",()=>{this.input.value="";});this.calc.querySelector(`.${this.calc.id}__result`).addEventListener("click",()=>{if(eval(this.input.value))
{this.input.value=eval(this.input.value);}});this.calc.querySelector(`.${this.calc.id}__delete`).addEventListener("click",()=>{if(this.input.value)
{this.input.value=this.input.value.slice(0,this.input.value.length-1);}});}}